  operator1 = `result :${value1 * value2}`;
  operator2 = `bool value2 : ${boolValue ? 'yes i can': 'no i can not'}`;
  console.log(operator1);
  console.log(operator2);


  let header = "Templates Literals";
  let subject = ["java", "jsp", "oracle","spring boot"];
  let html = `<h2>${header}</h2><ul>`;
