import styled from 'styled-components';

/* 화살표 함수 : (argument) => { statements } */
const TitleFontScale = styled.h2`
	font-size: 30px;
	font-weight: 600;
`

function App(){
	return (
    	<>
        	<TitleFontScale font-size="60px">큰 글자</TitleFontScale>
            <TitleFontScale font-size="20px">중간 크기 글자</TitleFontScale>
        </>
    )
}
export default App ;
