function App() {
  var members=['김유신','이순신'];
  var element01=members[0];
  var element02=members[1];
  console.log('ES5 element01 : ' + element01);
  console.log('ES5 element02 : ' + element02);

  var element03=members[2]||'윤봉길';
  console.log('|| 연산자를 이용하여 해당 인덱스가 없는 경우 기본 값을 지정할 수 있습니다.')
  console.log('ES5 element03 : ' + element03);


  var members2=['황진이','신사임당'];
  var [element04,element05,element06='유관순'] = members2;
  console.log('ES5 element04 : ' + element04);
  console.log('ES5 element05 : ' + element05);
  console.log('ES5 element06 : ' + element06);

  console.log('swapping 기법 ');
  var temp = element02;
  element02=element01;
  element01=temp;
  console.log('ES5 element01 : ' + element01);
  console.log('ES5 element02 : ' + element02);

  [element04,element05]=[element05,element04]
  console.log('ES5 element04 : ' + element04);
  console.log('ES5 element05 : ' + element05);


  var obj={
      name:'강감찬',
      age:30

  };


  var name= obj.name;
  var age=obj.age;
  var address = obj.address||'서울시 마포구 공덕동';
  console.log('ES5 name : ' + name);
  console.log('ES5 age : ' + age);
  console.log('ES5 address : ' + address);


  console.log('구조 분해를 이용한 값 할');
  var saram={
    name2:'이순신',
    age2:60,
  };
  const{name2,age2} = saram;
  console.log('ES6 name2 : ' + name2);
  console.log('ES6 age2 : ' + age2);

  console.log('...는 전개 연산자 (spread operator)');
  var members3=['김유신','이순신','강감찬','신사임당','임꺽정'];
  var [onehuman,second, ...humans]=members3;
  console.log('ES6 onehuman : ' + onehuman);
  console.log('ES6 humans : ' + humans);
  console.log('ES6 second : ' + second);

  var {...humaninfo} = {name:'김유신',age:30,address:'금천구 가산동'};
  console.log("ES6 humaninfo['name'] : " + humaninfo['name']);
  console.log("ES6 humaninfo['age'] : " + humaninfo['age']);
  console.log("ES6 humaninfo['address'] : " + humaninfo['address']);










  return (
    <div>
    	호호호호호 ~~ hohoho
    </div>
  );
}

export default App;
